defineParticle(({DomParticle, html}) => {

  const template = html`

  <style>
    :host {
      --tile-size: var(--tile-width, calc(100vw - 24px));
    }
    @media (min-width: 720px) {
      :host {
        --tile-size: var(--tile-width,  calc(50vw - 48px));
      }
    }
    @media (min-width: 1100px) {
      :host {
        --tile-size: var(--tile-width, calc(25vw - 48px));
      }
    }
  </style>

  <style>
    [item] {
      display: flex;
      flex-direction: column;
      position: relative;
      width: var(--tile-size, 300px);
      height: 288px;
      overflow: hidden;
      padding: 12px;
      box-sizing: border-box;
      color: white;
      background-repeat: no-repeat;
      background-size: cover;
      background-position: bottom;
      font-family: 'Google Sans', sans-serif;
    }
    [fade] {
      position: absolute;
      top: 0;
      right: 0;
      bottom: 0;
      left: 0;
      background: rgba(0, 0, 0, 0) linear-gradient(to bottom, rgba(0, 0, 0, 0) 25%, rgba(0, 0, 0, 1)) repeat 0 0;
    }
    [flexed] {
      flex: 1;
      flex-grow: 0;
      flex-shrink: 0;
    }
    [columns] {
      flex: 1;
      display: flex;
      align-items: flex-end;
      position: relative;
    }
    [col0] {
      flex: 1;
      overflow: hidden;
      text-overflow: ellipsis;
      padding-right: 16px;
    }
    [col1] {
      overflow-x: hidden;
      text-align: right;
    }
    [address] {
      font-size: 14px;
      font-weight: medium;
      opacity: 0.9;
      padding-bottom: 4px;
      line-height: 18px;
      letter-spacing: 0.25px;
    }
    [title] {
      font-size: 24px;
      line-height: 30px;
    }
    [rating] {
      font-size: 32px;
      line-height: 36px;
      letter-spacing: 1.3px;
      margin-bottom: 2px;
      width: 100%;
    }
    [stars-container] {
      display: inline-block;
      width: 56px;
      height: 12px;
      background-image: url(data:image/png;base64,);
      background-size: cover;
    }
    [stars] {
      height: 100%;
      background-image: url(data:image/png;base64,);
      background-size: cover;
      background-repeat: repeat no-repeat;
    }
  </style>

  <div item xen:style="{{image}}">
    <div fade></div>
    <div columns>
      <div col0>
        <div address>{{address}}</div>
        <div id="webtest-title" title>{{name}}</div>
      </div>
      <div col1>
        <div rating>{{rating}}</div>
        <div stars-container>
          <div stars xen:style="{{starStyle}}"></div>
        </div>
      </div>
    </div>
  </div>

  `;

  return class extends DomParticle {
    get template() {
      return template;
    }
    shouldRender({restaurant}) {
      return Boolean(restaurant);
    }
    render({restaurant}) {
      return Object.assign({
        starStyle: !restaurant.rating ? `visibility: hidden` : `width: ${Math.round(restaurant.rating / 5 * 100)}%`,
        image: {backgroundImage: `url("${restaurant.photo}")`}
      }, restaurant.rawData);
    }
  };

});
