defineParticle(({DomParticle, html}) => {

  let host = `restaurant-detail`;

  let styles = html`
<style>
  [${host}] {
    position: relative;
    font-family: 'Google Sans', sans-serif;
    display: flex;
    flex-direction: column;
    width: 100%;
  }
  /*
  [${host}] star {
    display: inline-block;
    width: 14px;
    height: 1em;
    vertical-align: middle;
    background-repeat: no-repeat;
    background-size: 14px 13px;
    background-image: url(data:image/png;base64,);
  }
  [${host}] star[hollow] {
    background-image: url(data:image/png;base64,);
  }
  */
  [${host}] [content] {
    position: relative;
    top: 168px;
    padding: 0 16px 32px 16px;
  }
  [${host}] [name] {
    font-size: 28px;
    font-weight: medium;
    line-height: 36px;
    color: white;
    display: flex;
    width: 80%;
    height: 108px;
    align-items: flex-end;
    margin-bottom: 12px;
  }
  [${host}] [banner] {
    height: 288px;
    background-size: cover;
    position: absolute;
    left: 0;
    top: 0;
    right: 0;
    background-position: center center;
  }
  [${host}] [scrim] {
    position: absolute;
    top: 60%;
    right: 0;
    bottom: 0;
    left: 0;
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0.4) 100%) repeat 0 0;
  }
  [${host}] [scrimTop] {
    position: absolute;
    top: 0;
    right: 0;
    height: 80px;
    left: 0;
    background: rgba(255, 255,255, 0) linear-gradient(to bottom, rgba(255,255, 255, 0.4) 0px, rgba(255, 255, 255, 0) 100%) repeat 0 0;
  }
  [${host}] .close-button {
    padding: 16px;
    position: absolute;
    top: 0;
    right: 0;
    z-index: 10;
  }
  [${host}] [rating] {
    font-size: 56px;
    line-height: 56px;
    letter-spacing: 2px;
    text-align: center;
    margin-bottom: 2px;
    width: 100%;
  }
  [${host}] [stars-container] {
    display: inline-block;
    width: 82px;
    height: 18px;
    background-image: url(data:image/png;base64,);
    background-size: cover;
  }
  [${host}] [stars] {
    height: 100%;
    background-image: url(data:image/png;base64,);
    background-size: cover;
    background-repeat: repeat no-repeat;
  }
  [${host}] [row] {
    display: flex;
    flex-direction: row;
  }
  [${host}] [flex] {
    flex: 1;
  }
  [${host}] [detail-caption] {
    font-size: 14px;
    font-weight: 500;
    line-height: 14px;
    letter-spacing: .25px;
    opacity: .54;
    margin-bottom: 8px;
  }
  [${host}] [detail-content] {
    font-size: 18px;
    line-height: 24px;
    margin-bottom: 18px;
  }
  [${host}] [control-panel] {
    box-shadow: 0 0 8px 0px rgba(0,0,0,.15);
    border-radius: 8px;
  }
</style>
  `;

  let template = html`
${styles}
<div ${host}>
  <div banner style%="{{style}}">
    <div scrim></div>
    <div scrimTop></div>
  </div>

  <div content>
    <div name>{{name}}</div>
    <div row>
      <div flex style="margin-top: 22px;">
        <div detail-caption>Address</div>
        <div detail-content unsafe-html="{{addr}}"></div>
        <div detail-caption>Phone</div>
        <div detail-content>{{phone}}</div>
        <div detail-caption>Website</div>
        <div detail-content>{{website}}</div>
      </div>
      <div style="margin-top: 16px; width: 82px; align-items: flex-end; display: flex; flex-direction: column; padding-bottom: 4px;">
        <div rating>{{rating}}</div>
        <div stars-container>
          <div stars style="{{starStyle}}"></div>
        </div>
      </div>
    </div>

    <div control-panel slotid="action"></div>

  </div>
</div>
    `.trim();

  let services = `https://xenonjs.com/services/http/php`;
  let detailsService =`${services}/place-details.php`;

  return class extends DomParticle {
    get template() {
      return template;
    }
    willReceiveProps(props) {
      let {selected} = props;
      if (selected) {
        let item = selected;
        if (item && item.id) {
          this._fetchDetail(item.reference);
        }
        this._setState({item});
      }
    }
    _fetchDetail(reference) {
      fetch(`${detailsService}?reference=${reference}`)
        .then(response => response.json())
        .then(json => this._receiveDetail(json.result))
        ;
    }
    _receiveDetail(detail) {
      // console.log(detail);
      this._setState({detail});
    }
    shouldRender(props, state) {
      return Boolean(state.item);
    }
    render(props, state) {
      let model = {
        style: {
          backgroundImage: `url(${state.item.photo})`
        },
        name: state.item.name,
        rating: '',
        reviews: ''
      };
      let detail;
      if (state.detail) {
        let url =
            state.detail.website &&
            state.detail.website.replace(/^(?:https?:\/\/)?(?:www\.)?/i, '').split('/')[0];
        detail = {
          rating: state.detail.rating,
          starStyle: `width: ${Math.round( (state.detail.rating || 0) / 5 * 100)}%`,
          reviews: state.detail.reviews ? state.detail.reviews.length : 0,
          kind: state.detail.types ? state.detail.types.slice(0, 3).join(' - ').replace(/_/g, ' ') : '',
          addr: state.detail.vicinity,
          website: url || '(none)',
          phone: state.detail.formatted_phone_number || '(none)',
        };
      }
      return Object.assign(model, detail);
    }
  };

});
