defineParticle(({DomParticle, html}) => {

  const item = html`
<div item style%="{{image}}" style="position: relative;">
  <div scrim></div>
  <div style="flex: 1"></div>

  <div style="flex: 1; display: flex; position: relative; align-items: flex-end;">
    <div style="flex: 1;padding-right: 60px;">
      <div address>{{address}}</div>
      <div id="webtest-title" title>{{name}}</div>
    </div>
    <div style="width: 56px; align-items: flex-end; display: flex; flex-direction: column; padding-bottom: 4px;">
      <div rating>{{rating}}</div>
      <div stars-container>
        <div stars style="{{starStyle}}"></div>
      </div>
    </div>
  </div>
</div>
<div slotid="annotation" subid="{{id}}" style="padding:4px 0;">

`;

  const selectable = html`
<div selectable-item on-click="_onSelect" key="{{index}}" trigger$="{{name}}">
  ${item}
</div>
  `;

  const host = `master`;

  const template = html`
<style>
  [${host}] {
    overflow: auto;
  }
  [${host}] [selectable-item] {
    cursor: pointer;
    box-sizing: border-box;
  }

  @media (min-width: 960px) {
    [${host}] [selectable-item]{
      width: 50%;
      display: inline-block;
      padding-left: 4px;
      padding-right: 4px;
    }
    [${host}] {
      padding: 8px 4px;
    }
  }
  @media (min-width: 1280px) {
    [${host}] [selectable-item]{
      width: 33.33%;
      display: inline-block;
    }
  }
  [${host}] [selectable-item]:last-child {
    border-bottom: none;
  }
  [${host}] [item] {
    font-family: 'Google Sans', sans-serif;
    display: flex;
    flex-direction: column;
    color: white;
    background-repeat: no-repeat;
    background-size: cover;
    background-position: bottom;
    width: 100%;
    height: 288px;
    padding: 12px 16px;
    box-sizing: border-box;
  }
  [${host}] [scrim] {
    position: absolute;
    top: 60%;
    right: 0;
    bottom: 0;
    left: 0;
    background: rgba(0, 0, 0, 0) linear-gradient(to bottom, rgba(0, 0, 0, 0) 0px, rgba(0, 0, 0, 0.4) 100%) repeat 0 0;
  }
  [${host}] [address] {
    font-size: 14px;
    font-weight: medium;
    opacity: 0.9;
    padding-bottom: 4px;
    line-height: 18px;
    letter-spacing: .25px;
  }
  [${host}] [title] {
    font-size: 24px;
    line-height: 30px;
  }
  [${host}] [rating] {
    font-size: 36px;
    line-height: 36px;
    letter-spacing: 1.3px;
    text-align: center;
    margin-bottom: 2px;
    width: 100%;
  }
  [${host}] [stars-container] {
    display: inline-block;
    width: 56px;
    height: 12px;
    background-image: url(data:image/png;base64,);

    background-size: cover;
  }
  [${host}] [stars] {
    height: 100%;
    background-image: url(data:image/png;base64,);
    background-size: cover;
    background-repeat: repeat no-repeat;
  }
</style>

<div ${host}>
  <div slotid="modifier"></div>
  <x-list items="{{items}}"><template>${selectable}</template></x-list>
</div>
    `;

  return class extends DomParticle {
    get template() {
      return template;
    }
    willReceiveProps(props) {
      let items = props.list.map(({rawData}, i) => {
        return Object.assign({
          index: i,
          starStyle: `width: ${Math.round(rawData.rating / 5 * 100)}%`,
          image: {backgroundImage: `url("${rawData.photo}")`}
        }, rawData);
      });
      this._setState({items});
    }
    render(props, state) {
      return {
        items: state.items,
        count: state.items ? state.items.length : 0
      };
    }
    _onSelect(e, state) {
      this.handles.get('selected').set(this._props.list[e.data.key]);
    }
  };

});
